import * as React from "react";
import classnames from "classnames";
import styles from "./styles.module.css";

type Point = {
  x: number;
  y: number;
};

function calculatePath(start: Point, end: Point) {
  const a = start;
  const b = {
    x: start.x + (end.x - start.x) / 2,
    y: start.y + (end.y - start.y) / 2,
  };
  const ab = Math.sqrt((a.x - b.x) ** 2 + (a.y - b.y) ** 2);
  const v1x = (b.x = a.x) / ab;
  const v1y = (b.y - a.y) / ab;
  const v3x = (v1y > 0 ? -v1y : v1y) * (ab * 0.2);
  const v3y = (v1x > 0 ? -v1x : v1x) * (ab * 0.2);
  const cx = a.x + v3x;
  const cy = a.y + v3y;

  return `
    M ${start.x},${start.y} 
    Q ${cx},${cy} ${end.x},${end.y} 
  `;
}

const MAP_WIDTH = 1180;
const MAP_HEIGHT = 760;

const getXByLong = (longitude: number) =>
  ((MAP_WIDTH + 175) / 360.0) * (180 + longitude);
const getYByLong = (latitude: number) =>
  ((MAP_HEIGHT - 20) / 180.0) * (90 - latitude);

interface Node {
  count: number;
  latitude: number;
  longitude: number;
}

interface SVGProps {
  networks: Array<[[number, number], [number, number]]>;
  nodes: Node[];
  // add white and other themes if need
  theme?: "dark" | "light";
}

